   private void Eliminar(){
        try     
        {
            // toma el valor de la l√≠nea seleccionada
            index = tblCliente.getSelectedRow();
            // elimina la l√≠nea de la tabla
            DefaultTableModel model = (DefaultTableModel) tblCliente.getModel(); 
            if (index<0){
                JOptionPane.showMessageDialog(null,  "Debe seleccionar una fila de la tabla" );  
            }else {
                int confirmar=JOptionPane.showConfirmDialog(null, "Esta seguro que desea Eliminar el registro? ");
                if(JOptionPane.OK_OPTION==confirmar) {
                    model.removeRow(index);
                    JOptionPane.showMessageDialog(null, "Registro Eliminado" );
                }
            }
            // elimina la l√≠nea de la lista.
            tCliente = (cliente)cliente.lista.get(index);           
            cliente.Eliminar(tCliente);
            CargaLista();
        }
        catch(Exception e)
        {
            JOptionPane.showInputDialog(null, e);
        }
    }
    private void Modificar(){
   
       index= tblCliente.getSelectedRow();
     tCliente=(cliente)cliente.lista.get(index);
        txtnombre.setText(tCliente.nombre);
        txtCedula.setValue(tCliente.cedula);
        spinPeso.setValue(tCliente.peso);
        txtNum.setValue(tCliente.telefono);
        txtPago.setText(tCliente.fechapago);
    }
